sysauto_script.script:
// get today's date and time
var rightnow = new GlideDateTime();
// get 24 hours from now
var in24Hours = new GlideDateTime(rightnow.toString());
in24Hours.addSeconds(24 * 60 * 60);
gs.info("Right now = " + rightnow);
gs.info("24 hours later = " + in24Hours);
var needItTasksWithin24Hours = new GlideRecord('x_58872_needit_needit_task');
needItTasksWithin24Hours.addQuery('due_date', '<', in24Hours);
needItTasksWithin24Hours.addQuery('due_date', '>=', rightnow);

needItTasksWithin24Hours.query();

while(needItTasksWithin24Hours.next()) {
	gs.info("NeedIt Task record due soon = " + needItTasksWithin24Hours.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks', needItTasksWithin24Hours, needItTasksWithin24Hours.number, gs.getUserName());
}
